import React from "react";
import Cardlist from "./Cardlist";
import SearchBox from "./SearchBox";
import { robots } from "./robotList";

class App extends React.Component {
  constructor() {
    super();
    this.state = {
      number: 1,
      robot: robots,
      searchfield: null,
    };
  }

  onClick = (event) => {
    console.log(event);
  };

  onChange = (event) => {
    this.setState({ searchfield: event.target.value });
    // console.log(typeof this.state.robot);

    const toFilterRobot = () => {
      return robots.name
        .toLowerCase()
        .includes(this.state.searchfield.toLowerCase());
    };
    const finterRobots = this.state.robot.filter(toFilterRobot);
  };

  render() {
    return (
      <div>
        <button onClick={this.onClick}>yo</button>
        <h1>{this.state.number}</h1>
        <SearchBox a={this.onChange} />
        <Cardlist robot={rofinterRobotsbots} />;
      </div>
    );
  }
}

export default App;
